import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import './TelaAvancada.css'

const Card = ({ title, description, isSelected = false, onClick }) => (
  <div 
    className={`card ${isSelected ? 'selected' : ''}`}
    onClick={onClick}
  >
    <div className="cardzinho">
      <p className="title">Step 1 of 2</p>
      <div className="upload-section">
        <span className="uploadzinho">üîç</span>
        <h2 className="cardTitle">{title}</h2>
      </div>
      <p className="Descricaozinha">{description}</p>
    </div>
    
    <div className="upload">
      <span className="uploadzinhooo">üìÑ</span>
      <p>Upload & analyze</p>
    </div>
  </div>
);

const ReturnButton = () => {
  const navigate = useNavigate()
  return (
    <button className="Buttonzito" onClick={() => navigate("/MenuPrincipal")}>
      Return to mission
    </button>
  )
}

const UpdateButton = () => {
  const navigate = useNavigate()
  return (
    <button className="Buttonzito" onClick={() => navigate("/MenuPrincipal")}>
      Upload & analyze
    </button>
  )
}

function TelaAvancada() {
  const navigate = useNavigate();
  const [selectedCard, setSelectedCard] = useState(null)

  const handleCardClick = (cardTitle) => {
    setSelectedCard(cardTitle);
    alert('Upload data clicked! Implementar aqui o pr√≥ximo passo.')
  }

  return (
    <div className="fundaoSpace">
      <div className="top-ui">
        <img src="/logo2 (1).svg" alt="Logo" className="logo" />
        <ReturnButton />
      </div>

      <img src="../Nave_avan√ßada.png" alt="Rocket" className="navezinho" />
      <div className="quadro">
        <h2 className="modalTitleOne">üîç Upload your data set</h2>
        <h3 className="modalTitle">
          Upload a CSV file with star or light-curve data and let our AI co-pilot analyze it.</h3>
        <p className="modalTitle">
          Your ship will jump to the detected location in space, where the system will indentify and classify the object as planet or not.
        </p>
        <UpdateButton />
      </div>
    </div>
  )
}

export default TelaAvancada;
